import './App.css';
import React , { Component } from 'react'; 
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Rank from './components/Ranking/Ranking';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import 'tachyons'; 


/*// patch nodejs environment, we need to provide an implementation of
// HTMLCanvasElement and HTMLImageElement, additionally an implementation
// of ImageData is required, in case you want to use the MTCNN
const { Canvas, Image } = canvas
faceapi.env.monkeyPatch({ Canvas, Image});

const image = document.getElementById('myImg')*/

const app = new Clarifai.App({
  apiKey:'de24ba7f7eec497a95d403c448077725'
});


const Polygonparticles = {
                    polygon: {
                        enable: true,
                        type: 'inside',
                        move: {
                            radius: 10
                        }
                    }
                };

class App extends Component { 
  constructor(){
    super()
    this.state = {
      input : '', 
      imageUrl: '', 
      box: []
    }
  } 

  calculateFaceLocation = (response) => {
    const clarifaiBox = (response.outputs[0].data.regions[0].region_info.bounding_box);
    const image = document.getElementById('imageId');
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiBox.left_col * width, 
      rightCol: width - (clarifaiBox.rightCol * width),
      topRow: clarifaiBox.topRow * height,
      bottomRow: height - (clarifaiBox.bottomRow * height) 
     };
  }

  displayFaceBox = (box) => {
      this.setState({box:box});
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value}); 
  }

  onButtonChange = () => {
   this.setState({imageUrl: this.state.input})

    /*const detections = async () => { await faceapi.detectAllFaces(link) } 

    const myImg = document.getElementById('myImg')
    myImg.src = image.src */ 
    app.models.predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
    .then(response => 
        //console.log(response.outputs[0].data.regions[0].region_info.bounding_box);
        this.displayFaceBox(this.calculateFaceLocation(response))
       )
    .catch(err => {
        console.log(err);
       });


    console.log("click!");
  }
  render() {
    return (
      <div className="App">
      <Particles className= 'particles' params = {Polygonparticles}
        />
        <Navigation />
        <Logo /> 
        <Rank /> 
        <ImageLinkForm onInputChange={this.onInputChange} onButtonChange={this.onButtonChange} /> 
        <FaceRecognition box = {this.state.box} imageUrl={this.state.imageUrl}/>  
      </div> 
    );
  }
}
export default App;
