import './App.css';
import React , { Component } from 'react'; 
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Rank from './components/Ranking/Ranking';
import Particles from 'react-particles-js';
// implements nodejs wrappers for HTMLCanvasElement, HTMLImageElement, ImageData
import * as canvas from 'canvas';
import * as faceapi from 'face-api.js';


// patch nodejs environment, we need to provide an implementation of
// HTMLCanvasElement and HTMLImageElement, additionally an implementation
// of ImageData is required, in case you want to use the MTCNN
const { Canvas, Image } = canvas
faceapi.env.monkeyPatch({ Canvas, Image});

const Polygonparticles = {
                    polygon: {
                        enable: true,
                        type: 'inside',
                        move: {
                            radius: 10
                        }
                    }
                };

class App extends Component { 
  constructor(){
    super()
    this.state = {
      input : '', 
      imageUrl: 'https://images.unsplash.com/photo-1612896488082-7271dc0ed30c?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1528&q=80',
    }
  } 

  onInputChange = (event) => {
    this.setState({input: event.target.value}); 
  }

  onButtonChange = () => {
    this.setState({imageUrl: this.state.input})
    const image = document.getElementById('myImg')
    const detections = async () => { 
       const result = await faceapi.detectAllFaces(image)
       return result 
      } 

       // resize the detected boxes in case your displayed image has a different size then the original
    const detectionsForSize = faceapi.resizeResults(detections, { width: image.width, height: image.height })
    // draw them into a canvas
    const canvas = document.getElementById('myImg')
    canvas.width = image.width
    canvas.height = image.height
    faceapi.draw.drawDetections(canvas, detectionsForSize, { withScore: true })
    console.log("click!");
  }
  render() {
    return (
      <div className="App">
      <Particles className= 'particles' params = {Polygonparticles}
        />
        <Navigation />
        <Logo /> 
        <Rank /> 
        <ImageLinkForm onInputChange={this.onInputChange} onButtonChange={this.onButtonChange} /> 
        <FaceRecognition imageUrl={this.state.imageUrl}/>  
      </div> 
    );
  }
}
export default App;
